import React, {Component} from 'react';
import '../css/recomend.css';

const stockArry = ["AMZN","GOOGL","NFLX","MSFT","GOOG","WMT","JNJ","BRK.B","JPM","DIS", "s"];

for(var i=0; i<3; i++) {
  (function(i) {
      setTimeout(function() {
          console.log(stockArry[i]);
      },1000);
  })(i);
}
class Recomend extends Component{

    state = {
      stock: "",
    }
    render(){

      return(
        <div>
            <h3>{this.state.stock}</h3>
        </div>
    )
  }
}
export default Recomend
