  componentDidMount(){
    this.currentStock();
  }

  state = {
    stock: "",
    stockArry :["AMZN","GOOGL","NFLX","MSFT","GOOG","WMT","JNJ","BRK.B","JPM","DIS","s"],
    value: "",
    changePercentage: "",
  }

  searchData = query => {
    API.search(query)
    .then(res => {

      const dailySeries = res.data["Time Series (Daily)"];
      console.log(res.data)
    })
  }

  currentStock = () => {
    let i = 0;
    const interval = setInterval(() => {
    console.log(this.state.stockArry[i]);
    let switchedStock = this.state.stockArry[i];
    i += 1;
    this.setState({
      stock: switchedStock
    });
    if (i >= this.state.stockArry.length) {
      clearInterval(interval)
      this.currentStock();
      this.searchData(this.state.stock)
    }
    }, 4000);
  }

    render(){

      return(
        <div className="recomendBX"
        ><h3 className="stockTxt"
        >{this.state.stock}</h3>
        </div>
    )
  }
}
export default Recomend
